# This algorithm deletes the files that have been generated by the previous simulations
import os

def delete_file(file_path):
    if os.path.exists(file_path):  # Verifica se il file esiste
        os.remove(file_path)  # Rimuove il file
        print(f"Il file {file_path} Ã¨ stato eliminato con successo.")
    else:
        print(f"Il file {file_path} non esiste.")

